  const [compteur2, setCompteur2] = useState(0);
  return (
    <div className="bgdCompteurs">
      <span>Exemple de compteur </span>
      <button onClick={() => {
        console.log("incrémentation");
        setCompteur1(compteur1 + 1);
      }}>+</button>
      <button onClick={() => {
        console.log("incrémentation");
        setCompteur1(compteur1 - 1);
      }}>-</button>
      <span>Compteur 1 : {compteur1}</span>
    </div>
  );
};
