import React, { useState } from "react";
import "./Selecteurs.css";

const Selecteurs = () => {
    const [classNom, setClassNom] = useState("square");
    const [selectedSquare, setSelectedSquare] = useState("square");
    return (
        <div>

            <div onMouseOver={() => {
                setClassNom("mouseOver");
            }} onMouseOut={() => {
                setClassNom("square");
            }} className={classNom}></div>

            <div className="container">
                <div className={selectedSquare === 1 ? "square" : "mouseOver"} onClick={() => {
                    setSelectedSquare(1);
                }} >

                </div>
                <div className={selectedSquare === 2 ? "square" : "mouseOver"} onClick={() => {
                    setSelectedSquare(2);
                }} >
                </div>
                <div className={selectedSquare === 3 ? "square" : "mouseOver"} onClick={() => {
                    setSelectedSquare(3);
                }} >
                </div>
            </div>
        </div>
    );
};

export default Selecteurs;
